import React, { Component } from 'react';

class Minuman extends Component {
  state = {
    menu: ' ',
  };
  componentDidMount() {
    alert('Show Fashion! (Proses mount)');
  }
  ChangeFashion = () => {
    this.setState((state) => {
      if (state.menu === 'Es Teh = Rp. 5000') {
        return { menu: 'Es Buah = Rp. 10000' };
      } else {
        return { menu: 'Es Coca = Rp. 8000' };
      }
    });
  };
  componentWillUnmount() {
    alert('Hide Fashion (Proses Unmount)!');
  }
  render() {
    return (
      <div>
        <h3>(Pilihan Menu Minuman)</h3>
        <p>1. Es Teh = Rp. 5000</p>
        <p>2. Es Buah = Rp. 10000</p>
        <p>3. Es Coca = Rp. 8000</p>
        <button onClick={this.ChangeFashion}>Pilihan lain</button>
        <br />
        <p>{this.state.internet}</p>
      </div>
    );
  }
}
export default Minuman;
