import React, { Component } from 'react';

class Bayar extends Component {
  state = {
    makanan: ' ',
  };

  state = {
    minuman: '',
  };
  render() {
    return (
      <div style={{ width: '50%' }}>
        <button onClick={this.ChangeFashion}>Pilih Menu</button>
        <br />
        <p>
          Menu yang dipilih : {this.state.makanan} + {this.state.minuman}
        </p>
      </div>
    );
  }
}

export default Bayar;
