import React, { Component } from 'react';
class Makanan extends Component {
  state = {
    isi: 0,
  };

  componentDidMount() {
    alert('Show Makanan (Proses mount)');
  }

  componentWillUnmount() {
    alert('Hide Maknaan (Unmount)!');
  }

  tambahisi = () => {
    this.setState((state) => {
      return { isi: state.isi + 1 };
    });
  };
  kurangiisi = () => {
    this.setState((state) => {
      return { isi: state.isi - 1 };
    });
  };

  constructor(props) {
    super(props);
    this.handleChangeAsupan = this.handleChangeAsupan.bind(this);
    this.state = {
      menu: [
        ['Soto', 5000],
        ['Nasi Goreng', 10000],
        ['Mie Godok', 5000],
      ],
      pilih: {
        beliMakanan: 0,
      },
      totalMakanan: 0,
    };
  }

  render() {
    return (
      <div>
        <h3>(Menu Makanan)</h3>
        <p>1. Soto = Rp. 5.000</p>
        <p>2. Nasi Goreng= Rp. 10.000</p>
        <p>3. Mie Godok = Rp. 5.000</p>
        <h4>Pilih Makanan : {this.state.isi}</h4>

        <button onClick={this.state.menu}>Tambah</button>
        <button onClick={this.state.beliMakanan}>Kurang</button>
        <br />
        <p>{this.state.isi}</p>
      </div>
    );
  }
}
export default Makanan;
