  state = {
    isi: 0,
  };

  componentDidMount() {
    alert('Show Makanan (Proses mount)');
  }

  componentWillUnmount() {
    alert('Hide Maknaan (Unmount)!');
  }

  tambahisi = () => {
    this.setState((state) => {
      return { isi: state.isi + 1 };
    });
  };
  kurangiisi = () => {
    this.setState((state) => {
      return { isi: state.isi - 1 };
